/**
 * Our base Cache implementation
 * Extend this class with new implementations to create different cache types (in-memory, database, file system etc.)
 * @class
 */
export default class CacheBase {
  /**
     * @param {Object} options
     * @param {boolean} [options.useMemoryCache=true] Use an in-memory layer on top of this cache
     *  Avoid hitting databases too often
     *  Not useful if using any distributed setup where memory will be out-of-sync between processes
     * @param {(number|null)} [options.memoryCacheTimeout=null] Timeout for in-memory cache values
     *  Default is null, which will use the incoming ttl values for each key
     */
  constructor(options = {
    useMemoryCache: true,
    memoryCacheTimeout: null,
  }) {
    this.memoryLayerEnabled = options.useMemoryCache;
    this.memCache = {};

    // stack up multiple cache wraps so they wait for a single request to finish
    this.pendingCacheWraps = {};

    this.failedCacheWraps = {};
  }

  /**
     * Internal implementation of Get()
     * @param {string} key Unique key name for this cache entry
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     * @abstract
     * @private
     */
  async _get(key) {
    throw new Error('Missing Implementation CacheBase::_get(key)');
  }

  /**
     * Internal implementation of Set()
     * @param {string} key Unique key name for this cache entry
     * @param {Object} object Data to be set
     * @abstract
     * @private
     */
  async _set(key, object) {
    throw new Error('Missing Implementation CacheBase::_set(key, object)');
  }

  /**
     * Internal operation to delete a key
     * @param {string} key Key name to delete
     * @abstract
     * @private
     */
  async _del(key) {
    throw new Error('Missing Implementation CacheBase::_del(key)');
  }

  /**
     * Internal implementation of getKeys()
     * @param {string} prefix
     * @abstract
     * @private
     */
  async _getKeys(prefix) {
    throw new Error('Missing Implementation CacheBase::_getKeys(prefix)');
  }

  /**
     * Get a cached object
     * @param {string} key Unique key name for this cache entry
     * @param {boolean} [getFullObject] Get the full cache entry, including expiry time, even if expired
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async get(key, getFullObject = false) {
    const now = +new Date();

    // our optional in-memory cache goes first
    if (this.memoryLayerEnabled) {
      const cacheEntry = this.memCache[key];
      if (cacheEntry !== undefined) {
        if (getFullObject) {
          return cacheEntry;
        }

        if (cacheEntry.expires >= now) {
          return cacheEntry.value;
        }
      }
    }

    // then use our internal cache if we haven't got the value stored locally
    const cacheValue = await this._get(key);
    if (cacheValue !== undefined) {
      if (getFullObject) {
        return cacheValue;
      }

      if (cacheValue.expires >= now) {
        return cacheValue.value;
      }
    }

    return undefined;
  }

  /**
     * Set a key in our cache
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async set(key, value, ttl = 3600000) {
    // resolve our cache time
    let cacheTime = ttl;
    // if our cache time input is a function, resolve it and store the result (in milliseconds)
    if (typeof cacheTime === 'function') {
      cacheTime = await cacheTime();
    }

    // optionally keep an in-memory cache layer
    if (this.memoryLayerEnabled) {
      const memoryCacheTime = this.memoryCacheTimeout === null ?
          cacheTime :
          (Math.min(this.memoryCacheTimeout, cacheTime)
          );
      if (memoryCacheTime < 0) {
        if (this.memCache[key]) {
          delete this.memCache[key];
        }
      } else {
        this.memCache[key] = {
          value,
          expires: (+new Date()) + memoryCacheTime,
        };
      }
    }

    if (cacheTime < 0) {
      // delete key if ttl is negative
      await this._del(key);
    } else {
      // call the private _Set implementation to actually set the key
      this._set(key, {
        value,
        expires: (+new Date()) + cacheTime,
      });
    }
  }

  /**
     * A helper "wrap" function that will return a cached value if present
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrap(key, fn, ttl) {
    // if another system is already wrapping this key, return it's pending Promise
    if (this.pendingCacheWraps[key] !== undefined) {
      return this.pendingCacheWraps[key];
    }

    // wrap all await calls in another Promise that we store
    //  this allows multiple calls to Wrap to stack up, and they all get the same result
    this.pendingCacheWraps[key] = new Promise(async (resolve) => {
      // try and fetch the cached value
      const cachedValue = await this.get(key, true);

      // if not in our cache, call the supplied fetcher function
      if (cachedValue !== undefined) {
        // check timestamp to see if value is still valid
        const now = +new Date();
        if (cachedValue.expires > now) {
          // it is! return it!
          return resolve(cachedValue.value);
        }
        // it isn't! fall through and run our wrap function to get new data
      }

      let error = null;
      try {
        const newValue = await fn();

        // set the new value in our cache
        this.failedCacheWraps[key] = 0;
        await this.set(key, newValue, ttl);
        return resolve(newValue);
      } catch (e) {
        // store in case we want to throw this later
        error = e;
        console.error(`Error caching value ${key}`, e);
      }

      if (this.failedCacheWraps[key] === undefined) {
        this.failedCacheWraps[key] = 0;
      }
      this.failedCacheWraps[key]++;

      // failed! store old data briefly, then return old data back
      await this.set(key, cachedValue?.value, 1000 * 30); // try again in 30 seconds
      if (this.failedCacheWraps[key] > 5) {
        // report after multiple failures
        if (error !== null) {
          // throw the actual error if we had one
          throw error;
        }
        throw new Error(`Failed to resolve wrap function for cache key ${key}`);
      }
      return resolve(cachedValue?.value);
    });
    const cachedValue = await this.pendingCacheWraps[key];
    this.pendingCacheWraps[key] = undefined;

    // return the fetched or calculated value
    return cachedValue;
  }

  /**
   * Get an array of all the cached keys matching the supplied prefix
   * @param {string} [prefix='']
   * @return {array<string>}
   */
  async getKeys(prefix = '') {
    return this._getKeys(prefix);
  }
}

import CacheBase from './cacheBase.js';
import level from 'level';
import path from 'path';

/**
 * A cache implementation using LevelDown
 * @extends CacheBase
 * @class
 */
export default class CacheLevel extends CacheBase {
  /**
   * Create new Memory cache object
   * @param {object} options
   */
  constructor(options = {}) {
    options.useMemoryCache = false;

    super(options);

    // setup our Level database
    this.db = level(path.join(process.cwd(), 'db.cache'));
  }

  /**
   * @inheritdoc
   */
  async _get(key) {
    try {
      const cacheEntry = await this.db.get(key);
      if (cacheEntry !== undefined) {
        return JSON.parse(cacheEntry);
      }
    } catch (err) {
      // ignore NotFoundError, throw any other errors back up the chain
      if (err.name !== 'NotFoundError') {
        throw err;
      }
    }

    return undefined;
  }

  /**
   * @inheritdoc
   */
  async _set(key, object) {
    await this.db.put(key, JSON.stringify(object));
  }

  /**
   * @inheritdoc
   */
  async _del(key) {
    await this.db.del(key);
  }

  /**
   * @inheritdoc
   */
  async _getKeys(prefix) {
    return new Promise((resolve) => {
      const keys = [];
      const keyStream = this.db.createKeyStream();
      keyStream.on('data', (data) => {
        if (data.indexOf(prefix) === 0) {
          keys.push(data);
        }
      });
      keyStream.on('end', () => {
        return resolve(keys);
      });
    });
  }
}

import CacheBase from './cacheBase.js';

/**
 * A basic in-memory cache implementation
 * @extends CacheBase
 * @class
 */
export default class CacheMemory extends CacheBase {
  /**
   * Create new Memory cache object
   * @param {object} options
   */
  constructor(options) {
    // disable our internal memory cache, since this is exactly what this implementation is already doing
    options.useMemoryCache = false;

    super(options);

    this.cache = {};
  }

  /**
   * @inheritdoc
   */
  async _get(key) {
    const cacheEntry = this.cache[key];
    if (cacheEntry !== undefined) {
      return cacheEntry;
    }

    return undefined;
  }

  /**
   * @inheritdoc
   */
  async _set(key, object) {
    this.cache[key] = object;
  }

  /**
   * @inheritdoc
   */
  async _del(key) {
    if (this.cache[key]) {
      delete this.cache[key];
    }
  }

  /**
   * @inheritdoc
   */
  async _getKeys(prefix) {
    return Object.keys(this.cache).filter((key) => {
      return key.indexOf(prefix) === 0;
    });
  }
}

import {getCache} from '../cache.js';
import {reusePromiseForever} from '../reusePromises.js';

/**
 * A wrapper class for accessing the cache.
 * Prefixes a string before each key to avoid conflicts.
 */
export class ScopedCache {
  /**
   * Create a new ScopedCache object by passing in the key prefix descired
   * @param {string} keyPrefix
   * @param {number} [version] Cache version, bump this to invalidate existing cache entries for a scope
   */
  constructor(keyPrefix, version = 0) {
    this.prefix = keyPrefix;
    this.version = version;

    this.cache = null;
  }

  /**
   * Initialise the cache for this scope
   */
  async initCache() {
    return await reusePromiseForever(this, this._initCache);
  }

  /**
   * Internal cache initialisation
   */
  async _initCache() {
    if (this.cache) {
      return this.cache;
    }

    this.cache = await getCache();

    // check and flush cache if version mismatch
    const cacheVersion = await this.cache.get(this.generateScopedKey('%%version%%'));
    if (cacheVersion !== undefined && cacheVersion != this.version) {
      // find all cache entries with this scope, and remove them
      const keys = await this.cache.getKeys(`${this.prefix}_`);

      await Promise.allSettled(keys.map((key) => {
        // set expire date to 1 millisecond ago (this basically deletes it)
        return this.cache.setGlobal(key, {}, -1);
      }));
    }

    // set our new cache version with a very very long ttl
    await this.cache.set('%%version%%', this.generateScopedKey(this.version), Number.MAX_SAFE_INTEGER);
    this._initCachePromise = null;

    return this.cache;
  }

  /**
   * Generate a scoped key by adding our prefix to the incoming key
   * @param {string} inKey
   * @return {string} Scoped key
   */
  generateScopedKey(inKey) {
    return `${this.prefix}_${inKey}`;
  }

  /**
     * Get a cached object
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async get(key) {
    return this.getGlobal(this.generateScopedKey(key));
  }

  /**
     * Get a cached object from the global cache (skipping the scope prefix)
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @return {(Object|undefined)} Returns the object in the cache, or undefined if not present
     */
  async getGlobal(key) {
    const cache = await this.initCache();
    return cache.get(key);
  }

  /**
     * Set a key in our cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async set(key, value, ttl = 3600000) {
    return this.setGlobal(this.generateScopedKey(key), value, ttl);
  }

  /**
     * Set a key in our global cache, skipping the scoped prefix
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {Object} value
     * @param {(Function|number)} [ttl=3600000] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     *  Default 1 hour
     */
  async setGlobal(key, value, ttl = 3600000) {
    const cache = await this.initCache();
    return cache.set(key, value, ttl);
  }

  /**
     * A helper "wrap" function that will return a cached value if present
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrap(key, fn, ttl) {
    return this.wrapGlobal(this.generateScopedKey(key), fn, ttl);
  }

  /**
     * A helper "wrap" function that will return a cached value if present (in the global scope)
     *  This will call the supplied function to fetch it if the value isn't present in the cache
     * @public
     * @async
     * @param {string} key Unique key name for this cache entry
     * @param {function} fn Fetch function that will be called if the cache entry is not present
     * @param {(function|number)} [ttl] How long the cache entry should last in milliseconds
     *  Can be a number or a function that will return a number
     */
  async wrapGlobal(key, fn, ttl) {
    const cache = await this.initCache();
    return cache.wrap(key, fn, ttl);
  }
}

export default ScopedCache;

/**
 * Cache provider for this module.
 * Various cache types are available, we default to using "leveldown" via the Level library.
 */

import CacheLevel from './cache/cacheLevel.js';

// our global cache instance
let CacheInstance = null;

/**
 * Create a new Cache Instance.
 * This function should only be called once for the lifetime of the module.
 */
async function createCacheInstance() {
  return new CacheLevel();
}

/**
  * Get the configured Cache implementation
  */
export async function getCache() {
  if (CacheInstance === null) {
    CacheInstance = await createCacheInstance();
  }

  return CacheInstance;
}

export default {
  getCache,
};

// load any environment variables from .env (if it exists)
import DotEnv from 'dotenv';
DotEnv.config();

import EventEmitter from 'events';

/**
 * Combine given options with environment variables
 * @param {object} options Incoming options
 * @return {object} Processed configuration options
 */
export function parseConfig(options = {}) {
  const configKeys = Object.keys(options);

  const config = {};

  options.configPrefixes = ['TPAPI'].concat(
      options.configPrefixes || [],
  );

  // build this.config object with our settings
  configKeys.forEach((key) => {
    // default prefixes are either "classname_" or "TPAPI"
    //  classes can add more with configPrefixes
    options.configPrefixes.forEach((prefix) => {
      const configEnvName = `${prefix}_${key}`.toUpperCase();

      if (process.env[configEnvName]) {
        // console.log(`Using env variable ${configEnvName}`);
        config[key] = process.env[configEnvName];
        // console.log(` ${key}(env.${configEnvName})=${config[key]}`);
      }
    });

    if (config[key] === undefined) {
      config[key] = options[key];
    } else {
      // convert env variable to number if the base default is a number
      if (typeof config[key] === 'number') {
        config[key] = Number(config[key]);
      } else if (typeof config[key] === 'boolean') {
        // convert any boolean configs too
        config[key] = (config[key] === 'true');
      }
    }
  });

  return config;
}

/**
 * Base Config Object
 * Supports classes with a single argument "options"
 * These will be sorted into a member called "this.config" containing all the same keys
 *
 * Crucially, these can also be overriden through environment variables
 * For example, for a config option "timeout" for class Database, this could be overriden through either:
 *   env.TPAPI_TIMEOUT (using a "global module name")
 *
 * Classes can also add additional prefixes to the supported environment variables through:
 *   new ClassInstance({configPrefixes: ['myCustomPrefix']});
 * Which would also allow env.MYCUSTOMPREFIX_TIMEOUT to be used
 *
 * Note that a default value must be supplied for the environment variable to be processed
 * If the default value is a number, the environment variable will be cast to a number as well
 * @class
 */
export class ConfigBase extends EventEmitter {
  /**
   * A base class that can be configured through environment variables
   * @param {object} options Config for instantiating this object
   */
  constructor(options = {}) {
    super();

    options.configPrefixes = [this.constructor.name].concat(
        options.configPrefixes || [],
    );

    this.config = parseConfig(options || {});
  }
}

export default ConfigBase;

/**
* Import all the parks included in the library
*/
// Efteling
import Efteling from './parks/efteling/efteling.js';
// EuropaPark
import EuropaPark from './parks/europapark/europapark.js';
// Compagnie des Alpes
import WalibiBelgium from './parks/walibi/walibibelgium.js';
import WalibiHolland from './parks/walibi/walibiholland.js';

/**
* Export all parks as string
*/
export default {
  parks: {
    // Efteling
    Efteling,
    // EuropaPark
    EuropaPark,
    // Compagnie des Alpes
    WalibiBelgium,
    WalibiHolland,
  },
  allParks: [
    // Efteling
    Efteling,
    // Europa-Park
    EuropaPark,
    // Compagnie des Alpes
    WalibiBelgium,
    WalibiHolland,
  ],
};

import moment from 'moment-timezone';
import fetch from 'node-fetch';
import {queueType, entityType} from '../types.js';
import {Park} from '../park.js';

import dotenv from 'dotenv';
dotenv.config();

/**
* Efteling Park Object
* Make sure all environment variables are set in an .env file which should be in the main location.
* Not setting these variables will make the module exit early without returning data.
*
* This class is here to fetch the POI data and to attach queue times data to it.
* After the fetches this data is send to the end user and from there he could do whatever he wants to do.
*
* Most park specific parameters are set already
* @class
*/
export class Efteling extends Park {
  /**
  * Create a new Efteling Park object
  * @param {object} options
  */
  constructor(options = {}) {
    options.name = 'Efteling';
    options.timezone = 'Europe/Amsterdam';

    // Setting the parks entrance as it's default location
    options.latitude = 51.65098350641645;
    options.longitude = 5.049916835374731;

    // Options for our park Object
    options.supportswaittimes = 'true';
    options.supportsschedule = 'true';
    options.supportsrideschedules = 'false';
    options.fastPass = 'true';
    options.FastPassReturnTimes = 'false';

    // Api options
    options.apiKey = process.env.EFTELING_API_KEY;

    options.searchURL = process.env.EFTELING_SEARCH_URL;
    options.waitTimesURL = process.env.EFTELING_WAITTIMES_URL;
    options.histURL = process.env.EFTELING_HIST_URL;

    // Language options
    options.languages = process.env.LANGUAGES;

    options.langoptions = `{'en', 'fr', 'de', 'nl'}`;

    super(options);

    // Check for existance
    if (!this.config.searchURL) throw new Error('Missing Efteling search url!');
    if (!this.config.apiKey) throw new Error('Missing Efteling apiKey!');
    if (!this.config.waitTimesURL) throw new Error('Missing Efteling waittimes url!');
    if (!this.config.histURL) throw new Error('Missing Efteling Operating Hours url!');
    if (!this.config.languages) {
      this.config.languages = 'en';
    };
  }

  /**
  * Get Efteling POI data
  * This data contains general ride names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.getPois().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling POIS without queuetimes
  */
  async getPOIS() {
    return await this.cache.wrap('poidata', async () => {
      return fetch(this.config.searchURL +
        `search?q.parser=structured&size=1000&q=(and (phrase field%3Dlanguage '$${this.config.languages}'))`,
      {
        method: 'GET',
        headers: {
          Authorization: this.config.apiKey,
        },
      },
      )
          .then((res) => res.json())
          .then((data) => {
            if (!data) throw new Error('No PoiData received from Efteling!'); // We didn't get any data
            const poiData = {};

            data.hits.hit.forEach((hit) => {
              if (hit.hide_in_app === 'true') return; // We don't want data that isn't displayed anyway

              let singlerider = undefined;

              if (hit.fields.alternateid && hit.fields.alternateid.indexOf('singlerider') === 0) {
                singlerider = 'true';
              } else {
                singlerider = 'false';
              }
              // Base rides & pois
              if (hit.fields) {
                poiData[hit.fields.id] = {
                  singlerider: singlerider,
                  name: hit.fields.name,
                  id: hit.fields.id,
                  type: hit.fields.category,
                  properties: hit.fields.properties,
                  area: hit.fields.empire,
                  description: hit.fields.detail_text,
                  short_description: hit.fields.subtitle,
                  label: undefined,
                  category: hit.fields.targetgroups,
                };

                // Define The six Swans & Single Rider Queues
                if (hit.fields.alternateid && hit.fields.alternatelabel === 'Boat ride') {
                  poiData[hit.fields.alternateid] = {
                    singlerider: 'false',
                    name: hit.fields.name,
                    id: hit.fields.alternateid,
                    type: hit.fields.category,
                    properties: hit.fields.properties,
                    area: hit.fields.empire,
                    description: hit.fields.detail_text,
                    short_description: hit.fields.subtitle,
                    label: hit.fields.alternatelabel,
                    category: hit.fields.targetgroups,
                  };
                } else if (hit.fields.alternateid && hit.fields.alternateid.indexOf('singlerider')) {
                  poiData[hit.fields.id].singleRiderId = hit.fields.alternateid;
                  poiData[hit.fields.alternateid] = {
                    singlerider: 'true',
                    name: hit.fields.name + ' Single Rider',
                    id: hit.fields.alternateid,
                    type: hit.fields.category,
                    properties: hit.fields.properties,
                    area: hit.fields.empire,
                    description: hit.fields.detail_text,
                    short_description: hit.fields.subtitle,
                    label: hit.fields.alternatelabel,
                    category: hit.fields.targetgroups,
                  };
                }

                // Get the location tags
                if (hit.fields.latlon) {
                  const latsplit = /([0-9.]+),([0-9.]+)/.exec(hit.fields.latlon);
                  if (latsplit) {
                    poiData[hit.fields.id].location = {
                      latitude: Number(latsplit[1]),
                      longitude: Number(latsplit[2]),
                    };
                  }
                  if (latsplit && hit.fields.alternateid) {
                    poiData[hit.fields.alternateid].location = {
                      latitude: Number(latsplit[1]),
                      longitude: Number(latsplit[2]),
                    };
                  }
                }
              }
            });
            return poiData;
          });
    }, 1000 * 60 * 60 * 12 /* cache for 12 hours */);
  }

  /**
  * Build Efteling ride object
  * This data contains general ride names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.buildRidePOI().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling ride POIS without queuetimes
  */
  async buildRidePOI() {
    const poiData = await this.getPOIS();

    if (!poiData) throw new Error('No PoiData for Efteling found!');
    const pois = {};
    let minHeightCM = undefined;
    let minHeightComp = undefined;
    let wet = undefined;
    let dizzyn = undefined;
    let preg = undefined;
    let weelchairtag = undefined;
    let familypoi = undefined;
    let thrillpoi = undefined;
    let youngpoi = undefined;
    let fastPass = undefined;
    Object.keys(poiData).forEach((poi) => {
      if (poiData[poi].id === 'dezeszwanenrondvaart' || poiData[poi].type === 'attraction') { // Yeah, somehow TSS is listed as fairytale.
        // Ugly Python FP Tweak
        if (poiData[poi].name === 'Python') {
          fastPass = 'true';
        } else {
          fastPass = 'false';
        }
        // Efteling tags
        if (poiData[poi].category) {
          const Thrill = poiData[poi].category.find((prop) => prop.indexOf('thrillseekers') === 0);
          const Young = poiData[poi].category.find((prop) => prop.indexOf('youngest-ones') === 0);
          const Family = poiData[poi].category.find((prop) => prop.indexOf('whole-family') === 0);

          if (Thrill !== undefined) {
            thrillpoi = 'Thrillseekers';
          } else {
            thrillpoi = undefined;
          }
          if (Young !== undefined) {
            youngpoi = 'Youngest_Ones';
          } else {
            youngpoi = undefined;
          }
          if (Family !== undefined) {
            familypoi = 'Family';
          } else {
            familypoi = undefined;
          }
        }

        if (poiData[poi].properties) {
          const minHeightProp = poiData[poi].properties.find((prop) => prop.indexOf('minimum') === 0);
          const minHeightComProp = poiData[poi].properties.find((prop) => prop.endsWith('undersupervision'));
          const maygetwet = poiData[poi].properties.find((prop) => prop.indexOf('wet') === 0);
          const weelchair = poiData[poi].properties.find((prop) => prop.indexOf('notaccessiblewheelchairs') === 0);
          const dizzyness = poiData[poi].properties.find((prop) => prop.indexOf('dizzy') === 0);
          const pregnant = poiData[poi].properties.find((prop) => prop.indexOf('pregnantwomen') === 0);
          if (minHeightProp !== undefined) {
            const minHeightNumber = Number(minHeightProp.slice(7));
            minHeightCM = minHeightNumber + ' cm';
          } else {
            minHeightCM = undefined;
          }

          if (minHeightComProp !== undefined) {
            const minHeightCompNumber = minHeightComProp.substring(0, 3);
            minHeightComp = minHeightCompNumber + ' cm';
          } else {
            minHeightComp = undefined;
          }

          if (maygetwet !== undefined) {
            wet = 'mayGetWet';
          } else {
            wet = undefined;
          }

          if (weelchair !== undefined) {
            weelchairtag = 'NotAccessibleWithWeelchair';
          } else {
            weelchairtag = undefined;
          }

          if (dizzyness !== undefined) {
            dizzyn = 'mayGetDizzy';
          } else {
            dizzyn = undefined;
          }

          if (pregnant !== undefined) {
            preg = 'UnsuitableForPregnantWomen';
          } else {
            preg = undefined;
          }
        }

        const restrictions = {
          minHeight: minHeightCM,
          minHeightAccompanied: minHeightComp,
        };

        pois[poiData[poi].id] = {
          name: poiData[poi].name,
          id: poiData[poi].id,
          waitTime: null,
          state: null,
          active: null,
          location: {
            area: poiData[poi].area,
            latitude: poiData[poi].location.latitude,
            longitude: poiData[poi].location.longitude,
          },
          meta: {
            category: [
              thrillpoi,
              youngpoi,
              familypoi,
            ],
            description: poiData[poi].description,
            label: poiData[poi].label,
            short_description: poiData[poi].short_description,
            single_rider: poiData[poi].singlerider,
            fastPass: fastPass,
            type: entityType.ride,
            tags: [
              wet,
              weelchairtag,
              dizzyn,
              preg,
            ],
            restrictions: restrictions,
          },
        };
      }
    });
    return Promise.resolve(pois);
  }

  /**
  * Build Efteling fairytale object
  * This data contains general fairytale names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.buildFairyTalePOI().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling fairytale POIS
  */
  async buildFairytalePOI() {
    const poiData = await this.getPOIS();

    if (!poiData) throw new Error('No PoiData for Efteling found!');
    const pois = {};
    Object.keys(poiData).forEach((poi) => {
      if (poiData[poi].id === 'dezeszwanenrondvaart' || poiData[poi].type === 'fairytale') {
        pois[poiData[poi].id] = {
          name: poiData[poi].name,
          id: poiData[poi].id,
          location: {
            area: poiData[poi].area,
            latitude: poiData[poi].location.latitude,
            longitude: poiData[poi].location.longitude,
          },
          meta: {
            description: poiData[poi].description,
            short_description: poiData[poi].short_description,
            type: entityType.fairytale,
          },
        };
      }
    });
    return Promise.resolve(pois);
  }

  /**
  * Build Efteling restaurant object
  * This data contains general restaurant names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.buildRestaurantPOI().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling restaurant POIS
  */
  async buildRestaurantPOI() {
    const poiData = await this.getPOIS();

    if (!poiData) throw new Error('No PoiData for Efteling found!');
    const pois = {};
    Object.keys(poiData).forEach((poi) => {
      if (poiData[poi].type === 'restaurant') {
        pois[poiData[poi].id] = {
          name: poiData[poi].name,
          id: poiData[poi].id,
          location: {
            area: poiData[poi].area,
            latitude: poiData[poi].location.latitude,
            longitude: poiData[poi].location.longitude,
          },
          meta: {
            description: poiData[poi].description,
            short_description: poiData[poi].short_description,
            type: entityType.restaurant,
          },
        };
      }
    });
    return Promise.resolve(pois);
  }

  /**
  * Build Efteling show object
  * This data contains general show names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.buildShowPOI().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling show POIS
  */
  async buildShowPOI() {
    const poiData = await this.getPOIS();

    if (!poiData) throw new Error('No PoiData for Efteling found!');
    const pois = {};
    let wet = undefined;
    let dizzyn = undefined;
    let anxious = undefined;
    let weelchairtag = undefined;
    Object.keys(poiData).forEach((poi) => {
      if (poiData[poi].type === 'show') {
        if (poiData[poi].properties) {
          const maygetwet = poiData[poi].properties.find((prop) => prop.indexOf('wet') === 0);
          const weelchair = poiData[poi].properties.find((prop) => prop.indexOf('notaccessiblewheelchairs') === 0);
          const dizzyness = poiData[poi].properties.find((prop) => prop.indexOf('dizzy') === 0);
          const anxious1 = poiData[poi].properties.find((prop) => prop.indexOf('anxiouseffects') === 0);

          if (maygetwet !== undefined) {
            wet = 'mayGetWet';
          } else {
            wet = undefined;
          }

          if (weelchair !== undefined) {
            weelchairtag = 'NotAccessibleWithWeelchair';
          } else {
            weelchairtag = undefined;
          }

          if (dizzyness !== undefined) {
            dizzyn = 'mayGetDizzy';
          } else {
            dizzyn = undefined;
          }

          if (anxious1 !== undefined) {
            anxious = 'AnxiousEffects';
          } else {
            anxious = undefined;
          }
        }

        pois[poiData[poi].id] = {
          name: poiData[poi].name,
          id: poiData[poi].id,
          location: {
            area: poiData[poi].area,
            latitude: poiData[poi].location.latitude,
            longitude: poiData[poi].location.longitude,
          },
          meta: {
            description: poiData[poi].description,
            short_description: poiData[poi].short_description,
            type: entityType.show,
            tags: [
              wet,
              weelchairtag,
              dizzyn,
              anxious,
            ],
          },
        };
      }
    });
    return Promise.resolve(pois);
  }

  /**
  * Build Efteling merchandise object
  * This data contains general merchandise names, descriptions etc.
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.buildMercnahdisePOI().then((pois) => {
  * console.log(pois)
  * });
  * @return {string} All Efteling merchandise POIS
  */
  async buildMerchandisePOI() {
    const poiData = await this.getPOIS();

    if (!poiData) throw new Error('No PoiData for Efteling found!');
    const pois = {};
    Object.keys(poiData).forEach((poi) => {
      if (poiData[poi].type === 'merchandise') {
        pois[poiData[poi].id] = {
          name: poiData[poi].name,
          id: poiData[poi].id,
          location: {
            area: poiData[poi].area,
            latitude: poiData[poi].location.latitude,
            longitude: poiData[poi].location.longitude,
          },
          meta: {
            description: poiData[poi].description,
            short_description: poiData[poi].short_description,
            type: entityType.merchandise,
          },
        };
      }
    });
    return Promise.resolve(pois);
  }

  /**
  * Get Efteling QueueTimes data
  * This data contains the queue data, we'll assign them to the earlier fetched pois
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.getQueue().then((queue) => {
  * console.log(queue)
  * });
  * @return {string} All Efteling POIS with queuetimes
  */
  async getQueue() {
    return await this.buildRidePOI().then((rideData) => fetch(this.config.waitTimesURL,
        {
          method: 'GET',
        },
    )
        .then((res) => res.json())
        .then((json) => {
          const rides = [];
          // Park is closed, and nothing is returned, attach that here.
          if (!json.AttractionInfo.length) {
            Object.keys(rideData).forEach((ride) => {
            // Update the variables to the closed rides
              rideData[ride].waitTime = '0';
              rideData[ride].state = queueType.closed;
              rideData[ride].active = 'false';

              // Create the ride Object
              const rideobj = {
                name: rideData[ride].name,
                id: 'Efteling_' + rideData[ride].id,
                waitTime: rideData[ride].waitTime,
                state: rideData[ride].state,
                active: rideData[ride].active,
                location: {
                  area: rideData[ride].location.area,
                  latitude: rideData[ride].location.latitude,
                  longitude: rideData[ride].location.longitude,
                },
                meta: {
                  category: rideData[ride].meta.category,
                  label: rideData[ride].meta.label,
                  description: rideData[ride].meta.description,
                  short_description: rideData[ride].meta.short_description,
                  type: rideData[ride].meta.type,
                  single_rider: rideData[ride].meta.single_rider,
                  fastPass: rideData[ride].meta.fastPass,
                  tags: rideData[ride].meta.tags,
                  restrictions: rideData[ride].meta.restrictions,
                },
              };
              rides.push(rideobj);
            });

            return Promise.resolve(rides);
          }

          // If there are rides listed, fetch them here.
          json.AttractionInfo.forEach((ridetime) => {
            let state = null;
            let active = null;
            let waitTime = null;

            if (ridetime.State === 'gesloten') { // Ride closed
              state = queueType.closed;
              active = 'false';
              waitTime = '0';
            } else if (ridetime.State === 'tijdelijkbuitenbedrijf') { // Ride went down for some reason
              state = queueType.down;
              active = 'false';
              waitTime = '0';
            } else if (ridetime.State === 'inonderhoud') { // Ride in maintenance
              state = queueType.refurbishment;
              active = 'false';
              waitTime = '0';
            } else if (ridetime.WaitingTime === undefined && ridetime.State === 'open') { // Ride is open, no queuetime reported
              state = queueType.operating;
              active = 'false';
              waitTime = '0';
            } else if (ridetime.WaitingTime === undefined && ridetime.State !== 'open') { // Ride is closed
              state = queueType.closed;
              active = 'false';
              waitTime = '0';
            } else { // Probably open, or some strange newly created frankensteined queue type
              state = queueType.operating;
              active = 'true';
              waitTime = ride.WaitingTime;
            }

            if (rideData[ridetime.Id]) {
              // Update the variables to the rides
              rideData[ridetime.Id].waitTime = waitTime;
              rideData[ridetime.Id].state = state;
              rideData[ridetime.Id].active = active;

              // Create the ride Object
              const rideobj = {
                name: rideData[ridetime.Id].name,
                id: 'Efteling_' + rideData[ridetime.Id].id,
                waitTime: rideData[ridetime.Id].waitTime,
                state: rideData[ridetime.Id].state,
                active: rideData[ridetime.Id].active,
                location: {
                  area: rideData[ridetime.Id].location.area,
                  latitude: rideData[ridetime.Id].location.latitude,
                  longitude: rideData[ridetime.Id].location.longitude,
                },
                meta: {
                  category: rideData[ridetime.Id].meta.category,
                  label: rideData[ridetime.Id].meta.label,
                  description: rideData[ridetime.Id].meta.description,
                  short_description: rideData[ridetime.Id].meta.short_description,
                  type: rideData[ridetime.Id].meta.type,
                  single_rider: rideData[ridetime.Id].meta.single_rider,
                  singleRider: rideData[ridetime.Id].meta.singleRider,
                  fastPass: rideData[ridetime.Id].meta.fastPass,
                  tags: rideData[ridetime.Id].meta.tags,
                  restrictions: rideData[ridetime.Id].meta.restrictions,
                },
              };
              rides.push(rideobj);
              return Promise.resolve(rides);
            }
          });
        }),
    );
  };

  /**
  * Get Efteling Park Hours data
  * This data contains the hours data, used to display the operating hours of Efteling
  * @example
  * import tpapi from '@alexvv13/tpapi';
  *
  * const park = new tpapi.park.Efteling();
  *
  * park.getOpHours().then((hours) => {
  * console.log(hours)
  * });
  * @return {string} All Efteling Operating Hours for 1mo
  */
  async getOpHours() {
    const currentYear = moment().format('YYYY');
    const currentMonth = moment().format('MM');

    return fetch(
        this.config.histURL +
        `${currentYear}/${currentMonth}`,
        {
          method: 'GET',
        },
    )
        .then((res) => res.json())
        .then((json) => {
          const Calendar = [];
          if (!json.OpeningHours.length) {
          // Park is closed, do nothing but returning today as empty string
            const hours = {
              date: moment().format('YYYY-MM-DD'),
              type: 'Closed',
              openingTime: moment('23:59', 'HH:mm a').format(),
              closingTime: moment('23:59', 'HH:mm a').format(),
              special: [],
            };
            Calendar.push(hours);
          } else {
          // Return the actual opening hours
            json.OpeningHours.forEach((cal) => {
              let date = moment.tz(`${cal.Date}`, 'YYYY-MM-DD', 'Europe/Amsterdam');
              date = moment(date).format('YYYY-MM-DD');
              cal.OpeningHours.forEach((cal1) => {
                let open = moment.tz(`${date}${cal1.Open}`, 'YYYY-MM-DDHH:mm', 'Europe/Amsterdam');
                open = moment(open).format();
                let close = moment.tz(`${date}${cal1.Close}`, 'YYYY-MM-DDHH:mm', 'Europe/Amsterdam');
                close = moment(close).format();
                const type = 'Operating';

                const hours = {
                  closingTime: close,
                  openingTime: open,
                  type: type,
                  special: [],
                  date: date,
                };
                Calendar.push(hours);
              });
            });
          };
          return Promise.resolve(Calendar);
        });
  }
};

export default Efteling;

